mapper.insert:
INSERT INTO t_strategy_accounts (
    strategy_instance_id, currency, balance, frozen, create_time, update_time
) VALUES (
    #{strategyInstanceId}, #{currency}, #{balance}, #{frozen}, NOW(), NOW()
)

mapper.update:
UPDATE t_strategy_accounts
SET balance = #{balance}, frozen = #{frozen}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT 
FROM t_strategy_accounts
WHERE strategy_instance_id = #{strategyInstanceId} AND currency = #{currency}
