mapper.insert:
INSERT INTO t_strategy_instances (
    strategy_id, user_id, strategy_name, strategy_type, status, exchange_id, trading_mode, interval_seconds, template_id, prompt_text, config_json, create_time, update_time
) VALUES (
    #{strategyId}, #{userId}, #{strategyName}, #{strategyType}, #{status}, #{exchangeId}, #{tradingMode}, #{intervalSeconds}, #{templateId}, #{promptText}, #{configJson}, NOW(), NOW()
)

mapper.update:
UPDATE t_strategy_instances
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE t_strategy_instances
SET status = #{status}, update_time = NOW()
WHERE strategy_id = #{strategyId}

UPDATE t_strategy_instances
SET config_json = #{configJson}, update_time = NOW()
WHERE id = #{id}

UPDATE t_strategy_instances
SET config_json = #{configJson}, update_time = NOW()
WHERE strategy_id = #{strategyId}

mapper.select:
SELECT 
FROM t_strategy_instances
WHERE user_id = #{userId} AND status = #{status}
